class CycleOptions():
    def __init__(self,opts):
        self.opts = opts
        self.cycle = itertools.cycle(opts)
    def __call__(self,*args,**kwargs):
        if args:
            kwargs = args[0]
        return kwargs | {'role':'chooser','choice':next(self.cycle)}

    def __str__(self):
        return f'''CycleOptions({",".join(self.opts)})'''

