with Define('hello world') as Schema:

    V('add',add,inputs=['Start(**=.)'],outputs=['End(add_result=.)'])
    V('square',Pow(2),inputs=['Start(a=b)'],outputs=['End(square_result=.)'])
Schema.draw()

